import React from 'react';
import {Navbar, Nav} from 'react-bootstrap';
import Container from 'react-bootstrap/Container';
import { FaCartPlus, FaUser } from 'react-icons/fa';
import {Link} from 'react-router-dom';
import PropTypes from 'prop-types';

class Barra extends React.Component{
    logged_out_nav = (
        <ul>
          <li onClick={() => this.props.display_form('login')}>login</li>
          <li onClick={() => this.props.display_form('signup')}>signup</li>
¿        </ul>
      );

      logged_in_nav = (
        <ul>
          <li onClick={this.props.handle_logout}>logout</li>
¿        </ul>
      );

    render(){
        return(
            <Navbar style={{position: "sticky", top: 0}} expand="lg" variant="light" bg="light" fixed="top">
                <Container>
                    <Navbar.Brand href="/">Tienda</Navbar.Brand>
                    <Nav>
                        <Nav.Link href="/carro">
                            <FaCartPlus/>
                            <Link to="/carro">Mi Carro</Link>  
                        </Nav.Link>
                        <Nav.Link eventKey={2}>
                            <FaUser/>
                            {this.props.logged_in ? this.logged_in_nav :this.logged_out_nav}                         
                        </Nav.Link>
                    </Nav>
                </Container>
            </Navbar>
        )
    }
}
export default Barra

 Barra.propTypes = {
  logged_in: PropTypes.bool.isRequired,
  display_form: PropTypes.func.isRequired,
  handle_logout: PropTypes.func.isRequired
};
