import React from 'react';
import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';
import * as actions from '../store/actions/auth';
import { Form, Input, Button, Checkbox } from 'antd';
import { Container, ButtonToolbar } from 'react-bootstrap';


class Demo extends React.Component {
    formRef = React.createRef();

    handleSubmit = (e) => {
      e.preventDefault();
      this.props.form.validateFields((err, values) => {
        if (!err) {
          alert("hoaaa")
          this.props.onAuth(values.username, values.password);
          this.props.history.push('/');
        }
      });
    }

    componentDidMount() {
      this.formRef.current.setFieldsValue({
        username: 'Bamboo',
      });
    }

    render() {

      return (
        <Container fluid>
            <Form ref={this.formRef} onSubmit={this.handleSubmit}> 
              <Form.Item name="username" rules={[{ required: true }]} placeholder="Username">
                  <Input />
              </Form.Item>
              <Form.Item name="password"  type="password" rules={[{ required: true, message: 'Please input your Password!' }]}>
                  <Input />
              </Form.Item>
              <Form.Item>
                  <Button type="primary" htmlType="submit">Apretame</Button>
              </Form.Item>
            </Form>
        </Container>    
        )
    }
  }


const mapStateToProps = (state) => {
    return {
        loading: state.loading,
        error: state.error
    }
}


const mapDispatchToProps = dispatch => {
    return {
        onAuth: (username, password) => dispatch(actions.authLogin(username, password)) 
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Demo)
