import React, { Component } from 'react';
import LoginForm from './components/LoginForm';
import SignupForm from './components/SignupForm';
import Home from './components/Home'
import 'bootstrap/dist/css/bootstrap.min.css';
import DetalleProducto from './components/DetalleProducto'
import PageNotFound from './components/PageNotFound'
import {BrowserRouter, Route, Switch, Redirect} from 'react-router-dom'


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      displayed_form: '',
      logged_in: localStorage.getItem('token') ? true : false,
      username: '',
      token: ''
    };
  }

  componentDidMount() {
    if (this.state.logged_in) {
      fetch('http://localhost:8000/core/current_user/', {
        headers: {
          Authorization: `JWT ${localStorage.getItem('token')}`
        }
      })
        .then(res => res.json())
        .then(json => {
          this.setState({ username: json.username });
        });
    }
  }

  handle_login = (e, data) => {
    e.preventDefault();
    fetch('http://localhost:8000/token-auth/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
      .then(res => res.json())
      .then(json => {
        localStorage.setItem('token', json.token);
        this.setState({
          logged_in: true,
          displayed_form: '',
          username: json.user.username,
          token: localStorage.getItem('token')
        });
        this.props.history.push('/');
      });
  };

  handle_signup = (e, data) => {
    e.preventDefault();
    fetch('http://localhost:8000/core/users/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
      .then(res => res.json())
      .then(json => {

        localStorage.setItem('token', json.token);
        this.setState({
          logged_in: true,
          displayed_form: '',
          username: json.username
        });
      });
  };

  handle_logout = () => {
    localStorage.removeItem('token');
    this.setState({ logged_in: false, username: '' });
  };

  display_form = form => {
    this.setState({
      displayed_form: form
    });
  };

  buscar = () => {
    fetch('http://localhost:8000/producto/1/', {
        headers: {
          Authorization: `JWT ${localStorage.getItem('token')}`
        }
      })
        .then(res => res.json())
        .then(json => {
          console.log(json)
        });
  }

  render() {
    /*
    let form;
    switch (this.state.displayed_form) {
      case 'login':
        form = <LoginForm handle_login={this.handle_login} />;
        break;
      case 'signup':
        form = <SignupForm handle_signup={this.handle_signup} />;
        break;
      default:
        form = null;
    }
    */
   let form = <LoginForm handle_login={this.handle_login} />;
    return (
      <div>
        <BrowserRouter>
            <div>
            <Switch>
              <Route exact path="/" component={Home} />
              <Route exact path="/producto/:id/" component={DetalleProducto} />
              <Route exact path="/login" component={<LoginForm handle_login={this.handle_login}/>}/>
              <Route exact path="/signin" component={<SignupForm handle_signup={this.handle_signup}/>} />
              <Route path="/404" component={PageNotFound} />
              <Redirect to="/404" />
              </Switch>
            </div>
          </BrowserRouter>
      </div>
    );
  }
}

export default App;
