import React from 'react';
import Barra from './Barra';
import Footer from './Footer';
import './assets/css/image-gallery.css';
import { FaCreditCard } from 'react-icons/fa';
import ImageGallery from 'react-image-gallery';
import {Container, Row, Col, Card} from 'react-bootstrap';



class DetalleProducto extends React.Component{

    render(){
        const descripcion = "Zapatillas marca adidas fenomenales. Nuevas"
        const precio = "$ 150.000"
        const images = [
            {
              original: 'https://picsum.photos/id/1018/1000/600/',
              thumbnail: 'https://picsum.photos/id/1018/250/150/',
            },
            {
              original: 'https://picsum.photos/id/1015/1000/600/',
              thumbnail: 'https://picsum.photos/id/1015/250/150/',
            },
            {
              original: 'https://picsum.photos/id/1019/1000/600/',
              thumbnail: 'https://picsum.photos/id/1019/250/150/',
            },
          ];

        return(
            <div>
                <Barra/>
                <br/>
                <Container fluid>
                    <Row>
                        <Col md={{ span: 6, offset: 1 }}>
                            <Container>
                                <ImageGallery items={images} />
                            </Container>
                        </Col>
                        <Col xs={6} lg={4}>
                            <Card>
                                <Card.Body>
                                    <Card.Title>
                                        <h3>{descripcion}</h3>
                                    </Card.Title>
                                    <br/>
                                    <Card.Title>
                                        <h1>{precio}</h1>
                                    </Card.Title>
                                    <br/>
                                    <Card.Title>
                                        <h6><FaCreditCard/> Paga en 12 Cuotas</h6>  
                                    </Card.Title>
                                    <br/>
                                    <span class="dc_payment_icons_bevel_48 dc_visa_bevel" title="Visa"></span>

                                </Card.Body>
                            </Card>
                        </Col>
                    </Row>
                </Container>
                <Footer/>
            </div>

        )
    }
}

export default DetalleProducto
